const initialState = {
    loading: false,
    user: [],
    error: '',

}

const user = (state = initialState, action) => {
    switch (action.type) {
        case "GET_USER_REQUEST":
            return {
                ...state,
                loading: true
            }
        case "GET_USER_SUCCESS":
            return {
                ...state,
                loading: false,
                user: action.payload,

            }
        case "GET_USERS_FAILURE":
            return {
                ...state,
                loading: false,
                user: {},
                error: action.payload
            }
        default:
            return state
    }
}
export default user;
