import React, { useEffect, useState } from 'react'
import { connect, useSelector, useDispatch } from 'react-redux'
import { fetchUsers } from './redux'
import { makeStyles } from '@material-ui/core/styles'
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import { Avatar } from "@material-ui/core";
import StarsIcon from '@material-ui/icons/Stars';
import Button from '@material-ui/core/Button';
import DetailDialog from './DetailDialog';
import Pagination from '@material-ui/lab/Pagination';

const useStyles = makeStyles(() => ({
    root: {
        margin: 'auto',
        width: '100%',
        maxWidth: 700,
        border: '1px solid gray',
    },
    pagination: {
        textAlign: 'center'
    }
}))

function UserContainer({ userData, fetchUsers }) {
    const classes = useStyles()
    const users = userData.users
    const loading = userData.loading
    const error = userData.error
    const [open, setOpen] = useState(false);
    const itemsPerPage = 10;
    const [page, setPage] = React.useState(1);

    const handleChange = (event, value) => {
        setPage(value);
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    useEffect(() => {
        fetchUsers()
    }, [])


    const renderList = () => {
        return users
            .slice((page - 1) * itemsPerPage, page * itemsPerPage)
            .map((user, i) => {
                const { avatar_url, login, site_admin } = user
                return (

                    <List component="nav" className={classes.root} >
                        <ListItem style={{ height: 50 }} key={i} >
                            <ListItemText primary={i + 1} />
                            <ListItemAvatar alignItems="flex-start">
                                <Avatar src={avatar_url} />
                            </ListItemAvatar>
                            <ListItemText> {login}</ListItemText>
                            <ListItemText> {site_admin ? <StarsIcon /> : ""} </ListItemText>
                            <ListItemText style={{ textAlign: 'right' }}>
                                <Button variant="outlined" color="primary" onClick={handleClickOpen}>
                                    Detail
                                </Button>
                                <DetailDialog open={open} close={handleClose} data={user} />
                            </ListItemText>

                        </ListItem>
                    </List>
                )
            }
            )
    }

    return loading ? (
        <h2>Loading</h2>
    ) : error ? (
        <h2>{error}</h2>
    ) : (
        <div><h2>User List</h2>
            {renderList()}
            <Pagination
                count={3}
                page={page}
                onChange={handleChange}
                defaultPage={1}
                color="primary"
                size="large"
                showFirstButton
                showLastButton
                classes={className.pagination}
            />
        </div>

    )
}
const mapStateToProps = state => {
    return {
        userData: state.users
    }
}
const mapDispatchToProps = dispatch => {
    return {
        fetchUsers: () => dispatch(fetchUsers())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
