import React, { useEffect, useState } from 'react'
import { connect, useSelector, useDispatch } from 'react-redux'
import { fetchUsers } from './redux'
import { Table, TableHead, TableRow, TableCell, makeStyles, TablePagination, TableSortLabel } from '@material-ui/core'
import { Avatar } from "@material-ui/core";
import StarsIcon from '@material-ui/icons/Stars';
import Button from '@material-ui/core/Button';
import DetailDialog from './DetailDialog';


const useStyles = makeStyles(() => ({
    root: {
        margin: 'auto',
        width: '100%',
        maxWidth: 700,
        border: '1px solid gray',
    },
}))

function UserContainer({ userData, fetchUsers }) {
    const pages = [5, 10, 25]
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(pages[page])
    const classes = useStyles()
    const users = userData.users
    const loading = userData.loading
    const error = userData.error
    const [open, setOpen] = useState(false);

    const TblContainer = props => (
        <Table className={classes.table}>
            {props.children}
        </Table>
    )

    const TblHead = props => {

        const handleSortRequest = cellId => {
            const isAsc = orderBy === cellId && order === "asc";
            setOrder(isAsc ? 'desc' : 'asc');
            setOrderBy(cellId)
        }

        return (<TableHead>
            <TableRow>
                {
                    headCells.map(headCell => (
                        <TableCell key={headCell.id}
                            sortDirection={orderBy === headCell.id ? order : false}>
                            {headCell.disableSorting ? headCell.label :
                                <TableSortLabel
                                    active={orderBy === headCell.id}
                                    direction={orderBy === headCell.id ? order : 'asc'}
                                    onClick={() => { handleSortRequest(headCell.id) }}>
                                    {headCell.label}
                                </TableSortLabel>
                            }
                        </TableCell>))
                }
            </TableRow>
        </TableHead>)
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const TblPagination = () => (<TablePagination
        component="div"
        page={page}
        rowsPerPageOptions={pages}
        rowsPerPage={rowsPerPage}
        count={records.length}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
    />)
    function stableSort(array, comparator) {
        const stabilizedThis = array.map((el, index) => [el, index]);
        stabilizedThis.sort((a, b) => {
            const order = comparator(a[0], b[0]);
            if (order !== 0) return order;
            return a[1] - b[1];
        });
        return stabilizedThis.map((el) => el[0]);
    }

    function getComparator(order, orderBy) {
        return order === 'desc'
            ? (a, b) => descendingComparator(a, b, orderBy)
            : (a, b) => -descendingComparator(a, b, orderBy);
    }

    function descendingComparator(a, b, orderBy) {
        if (b[orderBy] < a[orderBy]) {
            return -1;
        }
        if (b[orderBy] > a[orderBy]) {
            return 1;
        }
        return 0;
    }
    const recordsAfterPagingAndSorting = () => {
        return stableSort(filterFn.fn(records), getComparator(order, orderBy))
            .slice(page * rowsPerPage, (page + 1) * rowsPerPage)
    }

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    useEffect(() => {
        fetchUsers()
    }, [])


    const renderList = () => {
        return users.map((user, i) => {
            const { avatar_url, login, site_admin } = user
            return (

                <List component="nav" className={classes.root} >
                    <ListItem style={{ height: 50 }} key={i} >
                        <ListItemText primary={i + 1} />
                        <ListItemAvatar alignItems="flex-start">
                            <Avatar src={avatar_url} />
                        </ListItemAvatar>
                        <ListItemText> {login}</ListItemText>
                        <ListItemText> {site_admin ? <StarsIcon /> : ""} </ListItemText>
                        <ListItemText style={{ textAlign: 'right' }}>
                            <Button variant="outlined" color="primary" onClick={handleClickOpen}>
                                Detail
                            </Button>
                            <DetailDialog open={open} close={handleClose} data={user} />
                        </ListItemText>

                    </ListItem>
                </List>
            )
        }
        )
    }

    return loading ? (
        <h2>Loading</h2>
    ) : error ? (
        <h2>{error}</h2>
    ) : (
        <div><h2>User List</h2>
            {renderList()}
            <TablePagination
                rowsPerPageOptions={[1, 2, 3]}
                component="div"
                count={users.length}
                page={page}
                onPageChange={handleChangePage}
                rowsPerPage={rowsPerPage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />
        </div>

    )
}
const mapStateToProps = state => {
    return {
        userData: state.users
    }
}
const mapDispatchToProps = dispatch => {
    return {
        fetchUsers: () => dispatch(fetchUsers())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
