import React, { useEffect, useState } from 'react'
import { connect, useSelector, useDispatch } from 'react-redux'
import { fetchUsers } from './redux'
import { makeStyles } from '@material-ui/core/styles'
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import { Avatar } from "@material-ui/core";
import StarsIcon from '@material-ui/icons/Stars';
import Button from '@material-ui/core/Button';
import DetailDialog from './DetailDialog';
import TablePagination from '@material-ui/core/TablePagination';

const useStyles = makeStyles(() => ({
    root: {
        margin: 'auto',
        width: '100%',
        maxWidth: 700,
        border: '1px solid gray',
    },
}))

function UserContainer({ userData, fetchUsers }) {
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const classes = useStyles()
    const users = userData.users
    const loading = userData.loading
    const error = userData.error
    const [open, setOpen] = useState(false);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    useEffect(() => {
        fetchUsers()
    }, [])


    const renderList = () => {
        return users.map((user, i) => {
            const { avatar_url, login, site_admin } = user
            return (

                <List component="nav" className={classes.root} >
                    <ListItem style={{ height: 50 }} key={i} >
                        <ListItemText primary={i + 1} />
                        <ListItemAvatar alignItems="flex-start">
                            <Avatar src={avatar_url} />
                        </ListItemAvatar>
                        <ListItemText> {login}</ListItemText>
                        <ListItemText> {site_admin ? <StarsIcon /> : ""} </ListItemText>
                        <ListItemText style={{ textAlign: 'right' }}>
                            <Button variant="outlined" color="primary" onClick={handleClickOpen}>
                                Detail
                            </Button>
                            <DetailDialog open={open} close={handleClose} data={user} />
                        </ListItemText>

                    </ListItem>
                </List>
            )
        }
        )
    }

    return loading ? (
        <h2>Loading</h2>
    ) : error ? (
        <h2>{error}</h2>
    ) : (
        <div><h2>User List</h2>
            {renderList()}
            <TablePagination
                component="div"
                count={100}
                page={page}
                onPageChange={handleChangePage}
                rowsPerPage={rowsPerPage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />
        </div>

    )
}
const mapStateToProps = state => {
    return {
        userData: state.users
    }
}
const mapDispatchToProps = dispatch => {
    return {
        fetchUsers: () => dispatch(fetchUsers())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserContainer)
